import { userInfo } from "os";
import React, { useState } from "react";
import { Button } from "react-bootstrap";
import Container from 'react-bootstrap/Container';
import Row from 'react-bootstrap/Row';
import Col from 'react-bootstrap/Col';


export default function Counter() {
    const [totalCount, setTotalCount] = useState<number>(0);
    const [count, setCount] = useState<number>(0);
    const [bid, setBid] = useState<number>(0);

    const updateBid = () => {
        if (count === 108){
            setBid(bid + 1);
            setCount(0);
        }
    }

    const increment = () => {
        setTotalCount(totalCount + 1);
        setCount(count + 1);
        updateBid();
      };

      const decrement = () => {
        setTotalCount(totalCount - 1);
        setCount(count - 1);
        updateBid();
      }

      const resetCounts = () => {
        setTotalCount(0);
        setCount(0);
        setBid(0);
      }

      const keyDownHandler = (event: React.KeyboardEvent<HTMLDivElement>) => {
        console.log(event.code);

        if (event.code === "ArrowLeft") {
            decrement();
        }

        if (event.code === "ArrowRight") {
            increment();
        }
      };

      return (
        <div
          style={{
            display: 'flex',
            alignItems: 'center',
            justifyContent: 'center',
            minHeight: '100vh',
            minWidth: '100vw', // Set the minimum width of the div to 100vw
          }}
          tabIndex={0}
          onKeyDown={keyDownHandler}
        >
            <Row className="row-margin">Global Count : {totalCount}</Row>
            <Row className="row-margin">Current Count: {count}</Row>
            <Row className="row-margin">Bids : {bid}</Row>
            <Button onClick={increment}>Add</Button><br />
            <Button onClick={decrement}>Subtract</Button><br />
            <Button onClick={resetCounts}>Reset</Button><br />
        </div>
      );
}
