    medium: {
        [avatarSpacing]: tokens.spacingHorizontalS
    },
    large: {
        [avatarSpacing]: tokens.spacingHorizontalMNudge
    },
    'extra-large': {
        [avatarSpacing]: tokens.spacingHorizontalMNudge
    },
    huge: {
        [avatarSpacing]: tokens.spacingHorizontalM
    },
    after: {
        marginRight: `var(${avatarSpacing})`
