import { userInfo } from "os";
import React, { useState } from "react";
import { Button } from "react-bootstrap";
import Container from 'react-bootstrap/Container';
import Row from 'react-bootstrap/Row';
import Col from 'react-bootstrap/Col';


export default function Counter() {
    const [totalCount, setTotalCount] = useState<number>(0);
    const [count, setCount] = useState<number>(0);
    const [bid, setBid] = useState<number>(0);

    const updateBid = () => {
        if (count === 108){
            setBid(bid + 1);
            setCount(0);
        }
    }

    const increment = () => {
        setTotalCount(totalCount + 1);
        setCount(count + 1);
        updateBid();
      };

      const decrement = () => {
        setTotalCount(totalCount - 1);
        setCount(count - 1);
        updateBid();
      }

      const resetCounts = () => {
        setTotalCount(0);
        setCount(0);
        setBid(0);
      }

      const keyDownHandler = (event: React.KeyboardEvent<HTMLDivElement>) => {
        console.log(event.code);

        if (event.code === "ArrowLeft") {
            decrement();
        }

        if (event.code === "ArrowRight") {
            increment();
        }
      };

      return (
        <div style = {{
            display: 'flex',
            alignItems: 'center',
            justifyContent: 'center',
            }} tabIndex={0} onKeyDown={keyDownHandler}>
            <Container fluid style={{margin:"5%"}}>
            <Row>
            Global Count : {totalCount}
            </Row>
            <Row>
            Current Count: {count}
            </Row>
            <Row>
            Bids : {bid}
            </Row>
            <Button onClick={increment}>Add</Button><br />
            <Button onClick={decrement}>Subtract</Button><br />
            <Button onClick={resetCounts}>Reset</Button><br />
            </Container>
        </div>
      );
}
