    // style for label
    inverted: {
        color: 'rgba(255, 255, 255, 1)'
    },
    primary: {},
    'extra-tiny': {
        ...typographyStyles.body1
    },
    tiny: {
        ...typographyStyles.body1
    },
    'extra-small': {
        ...typographyStyles.body1
    },
    small: {
        ...typographyStyles.body1
    },
